query:
mutation createDeal($createdealinput: CreateDealInput!) {
  createDeal(input: $createdealinput) {
    id
    name
    subjectName
    subjectDescription
    subjectReference
    currency
    price
    type
    createdAt
    updatedAt
    associatedParties {
      items {
        userId
        userRoleType
      }
    }
    shippingPreference
    shippingDescriptionInperson
    shippingAddress {
        fullname
        street
        city
        zip
        country
    }
  }
}
